mapper.select:
SELECT distinct m.code,m.name FROM DATA_MAJOR m /*LEFT JOIN DATA_COLLEGE c on c.CODE = m.COLLEGE_CODE*/ 1=1
and COLLEGE_CODE = #{collegeCode}
ORDER BY NLSSORT(m.name,'NLS_SORT = SCHINESE_PINYIN_M')

SELECT T2.*
FROM DATA_TEACHER_COURSE T1 LEFT JOIN DATA_COURSE T2 ON T2.COURSE_CODE = T1.COURSE_CODE
WHERE T1.USER_ID =#{userId}
