mapper.select:
select sh.* ,m.menu_name ,shenheProcess ,(case when cs.shenhe_code is null then 1 else 0 end) is_delete /*数据状态，是否可以执行删除操作,可选值【0-不可删除，1可以删除】*/ from COMMON_SHENHE_SET sh LEFT JOIN sys_menu m on m.menu_id = sh.menu_id LEFT JOIN ( SELECT SHENHE_CODE,listagg(role_name,'>') within group(order by exec_level) shenheProcess FROM COMMON_SHENHE_NODE sn LEFT JOIN sys_role r ON r.role_id = sn.role_id GROUP BY SHENHE_CODE ) csn on csn.shenhe_code = sh.shenhe_code LEFT JOIN (SELECT DISTINCT shenhe_code from COMMON_SHENHE) cs on cs.shenhe_code = sh.shenhe_code
and m.pid = #{parentMenuId}
and m.menu_id = #{menuId}
ORDER BY sh.create_date

mapper.update:
update COMMON_SHENHE_SET


        shenhe_name = #{shenheName}


        , shenhe_desc = #{shenheDesc}


where shenhe_code = #{shenheCode}

delete.if:
begin

    delete from COMMON_SHENHE_SET where shenhe_code = #{code};
    delete from COMMON_SHENHE_NODE where shenhe_code = #{code};

end;
