mapper.select:
        SELECT t.CODE teacher_code,t.name teacher,t.MAJOR_NAME teacher_major,t.COLLEGE_NAME teacher_college
        ,c.course_code,c.course_name,c.course_attr
        ,jh.dates teach_date,jh.teach_addr
        ,(case when th.create_date is not null then 1 else 2 end) is_pj
        ,th.code as pj_code
        FROM DATA_TEACHER_COURSE tc
        LEFT JOIN DATA_TEACHER t on t.CODE = TC.USER_ID
        LEFT JOIN DATA_COURSE c on c.course_code = tc.course_code
        LEFT JOIN (
            SELECT * FROM JXSJ_SKJH jh LEFT JOIN JXSJ_SKJH_ITEM it on it.relation_code = jh.code
        ) jh on jh.course_code = c.course_code and jh.teacher = t.name
        LEFT JOIN JXPJ_THPJ th on teacher_CODE = TC.USER_ID and th.course_code = tc.course_code
        1=1

                and t.COLLEGE_NAME = #{teacherCollege}


                and t.MAJOR_NAME = #{teacherMajor}


                and t.name = #{teacher}





--                 and t.title = #{teacherTitle}


                and c.course_name like concat(concat('%',#{courseName}),'%')


                and c.course_attr = #{courseAttr}


                and jh.dates = to_date(#{teachDate}, 'yyyy-MM-dd')


                and jh.teach_addr = #{teachAddr}

mapper.update:
update JXPJ_THPJ


        test_name = #{testName}


        , remark = #{remark}


where code = #{code}
