mapper.select:
SELECT *
FROM JXYJ_JGLW
1=1

        and DY_AUTHOR_CODE = #{dyAuthorCode}


        and LW_TITLE = #{lwTitle}


        and QK_NAME = #{qkName}

mapper.update:
update JXYJ_JGLW


        lw_title = #{lwTitle}


        , qk_name = #{qkName}


        , qk_type = #{qkType}


        , dy_author_code = #{dyAuthorCode}


        , dy_author_name = #{dyAuthorName}


        , tx_author_code = #{txAuthorCode}


        , tx_author_name = #{txAuthorName}


        , publish_Year = #{publishYear}


        , publish_issue = #{publishIssue}


where code = #{code}
