choose.when:
select * from ( SELECT v.* ,(case when item.relation_code is not null then '已审核' else '未审核' end) shenhe_status ,memberIds,memberNames ,(case when ps.relation_code is not null and ps.middleResult = 1 then '已填写' else '未填写' end ) middleResult ,(case when ps.relation_code is not null and ps.finalResult = 1 then '已填写' else '未填写' end ) finalResult from V_JXYJ_JXTD_SHENHE v LEFT JOIN (SELECT * FROM COMMON_SHENHE_ITEM where USER_ID = #{shenHeUserId}) item on item.relation_code = v.code and item.batch_num = v.batch_num LEFT JOIN ( SELECT relation_code ,listagg(user_name,',') within group(order by user_id) memberNames ,listagg(user_id,',') within group(order by user_id) memberIds FROM JXYJ_JXTD_MEMBER GROUP BY relation_code ) m on m.relation_code = v.code left join ( SELECT RELATION_CODE,max(batch_num) batch_num ,max(case when pingshen_type = '中期考核' then 1 else 0 end ) middleResult ,max(case when pingshen_type = '最终考核' then 1 else 0 end ) finalResult from JXYJ_JXTD_PS WHERE USER_ID = #{shenHeUserId} GROUP BY RELATION_CODE ) ps on ps.relation_code = v.code and ps.batch_num = v.batch_num
) 1=1
and shenhe_status = #{shenheStatus}
ORDER BY shenhe_status DESC

choose.otherwise:
SELECT v.* from V_JXYJ_JXTD v 1=1
and shenhe_code is null
and status = #{status}

mapper.update:
update JXYJ_JXTD


        , team_name = #{teamName}


        , regist_date = #{registDate}


        , team_leader = #{teamLeader}


        , team_leader_id = #{teamLeaderId}


        , team_leader_unit = #{teamLeaderUnit}


        , sbs = #{sbs}


        , middle_report = #{middleReport}


        , middle_result = #{middleResult}


        , final_report = #{finalReport}


        , final_result = #{finalResult}


where code = #{code}

mapper.select:
SELECT * FROM JXYJ_JXTD_PS
WHERE RELATION_CODE = #{relationCode} AND BATCH_NUM =  #{batchNum} AND PINGSHEN_TYPE = #{pingshenType}

    AND USER_ID = #{userId}

ORDER BY CREATE_DATE
