choose.otherwise:
SELECT v.* ,(case when SH.create_date is not null then '已审核' else '未审核' end ) reportResult from v_JXYJ_JXTD v LEFT JOIN (SELECT * FROM COMMON_SHENHE_ITEM where SHENHE_TYPE = '终审') sh on SH.RELATION_CODE = v.CODE and SH.BATCH_NUM = v.BATCH_NUM 1=1
and shenhe_code is null
and status = #{status}

mapper.update:
update JXYJ_JXTD


        team_name = #{teamName}


        , regist_date = #{registDate}


        , declare_or_report = #{declareOrReport}


where code = #{code}

mapper.select:
SELECT *
FROM JXYJ_JXTD_ZJPS
WHERE RELATION_CODE = #{relationCode}
AND BATCH_NUM =  #{batchNum}

    AND USER_ID = #{userId}

ORDER BY CREATE_DATE
