choose.when:
SELECT v.* ,(case when exec_num >= exec_level then '已审核' else '未审核' end ) shenhe_status
/*,(case when exec_num >= exec_level then '已审核' else '未审核' end ) shenhe_status_first ,(case when exec_num > exec_level then '已审核' when exec_num = exec_level and isZjshAll =1 then '未审核' else '待审核' end ) shenhe_status_final*/
,(case when exec_num = exec_level and v.status = '未通过' then '未通过' when exec_num = exec_level and v.status = '退回' then '退回' when exec_num >= exec_level then '通过' else '未审核' end ) shenhe_status_first ,(case when exec_num > exec_level then v.status when exec_num = exec_level and (v.status = '未通过' or v.status = '退回') then '' when exec_num = exec_level and isZjshAll =1 then '未审核' else '待审核' end ) shenhe_status_final
from ( SELECT v.code,v.batch_num ,count(sn.node_code) total_num ,count(item.node_code) exec_num ,max(case when item.status = '未通过' or item.status = '退回' then 1 else 0 end ) state ,max(NVL(isZjshAll, 0)) isZjshAll from V_JXYJ_JGXM_SCHOOL_SHENHE v left join COMMON_SHENHE_NODE sn on sn.shenhe_code = v.shenhe_code left join COMMON_SHENHE_ITEM item on item.relation_code = v.code and item.batch_num = v.batch_num and item.node_code = sn.node_code LEFT JOIN ( SELECT xm_code,BATCH_NUM ,(case when count(0) = (SELECT count(0) FROM SYS_USER_ROLE where role_id = 7) then 1 else 0 end) isZjshAll from COMMON_SHENHE_ZJSH GROUP BY xm_code,BATCH_NUM ) zj on zj.xm_code = v.code and zj.batch_num = v.batch_num GROUP BY v.code,v.batch_num )t left join V_JXYJ_JGXM_SCHOOL_SHENHE v on v.code = t.code and v.batch_num = t.batch_num left join COMMON_SHENHE_NODE sn on sn.shenhe_code = v.shenhe_code left join sys_user_role ur on ur.role_id = sn.role_id 1=1 and exec_num >= exec_level-1 and 1 > (case when sn.exec_level > t.exec_num and state > 0 then 1 else 0 end) and ur.user_id = #{shenHeUserId}
and exec_num >= exec_level
and exec_level > exec_num
and exec_num >= exec_level and v.status = '审核中'
and exec_num = exec_level and v.status = '未通过'
and exec_num = exec_level and v.status = '退回'
and exec_level > exec_num
and exec_num = exec_level and isZjshAll =1
/*and exec_num <= exec_level*/ and isZjshAll !=1 /*and v.status != '未通过' and v.status != '退回'*/ and v.status = '审核中'
/*v.status = '通过' | '未通过' | '退回'*/ and exec_num > exec_level and v.status = #{shenheStatusFinal}
ORDER BY shenhe_status DESC

choose.otherwise:
SELECT v.* from V_JXYJ_JGXM_SCHOOL v 1=1
and shenhe_code is null
and status = #{status}

mapper.update:
update JXYJ_JGXM_SCHOOL


        xm_name = #{xmName}


        , xm_type = #{xmType}


        , leader = #{leader}


        , leader_id = #{leaderId}


        , title = #{title}


        , college_or_dept = #{collegeOrDept}


        , major = #{major}


        , main_teach_work = #{mainTeachWork}


        , main_teach_achievement = #{mainTeachAchievement}


        , current_and_background = #{currentAndBackground}


        , question_and_target = #{questionAndTarget}


        , expect_and_result = #{expectAndResult}


        , plan_and_process = #{planAndProcess}


where code = #{code}

mapper.delete:
delete from JXYJ_JGXM_SCHOOL_MEMBER
1=1

        and XM_CODE = #{xmCode}
        and USER_ID = #{userId}


        and XM_CODE = #{xmCode}

delete from JXYJ_JGXM_SCHOOL_FUND
1=1

        and XM_CODE = #{xmCode}
        and SUBJECT = #{subject}


        and XM_CODE = #{xmCode}
