mapper.select:
SELECT u.XY_NAME,u.USER_ID,u.USER_NAME,user_group FROM SYS_USER u LEFT JOIN ( SELECT user_id,wm_concat(role_name) user_group FROM SYS_USER_ROLE ur LEFT JOIN SYS_ROLE r on r.role_id = ur.role_id GROUP BY user_id ) ur on ur.USER_ID = u.USER_ID u.USER_ID != '111' /*超级管理员不应该显示在用户列表中*/
and u.XY_NAME = #{xyName}
and u.USER_ID = #{userId}
and u.USER_NAME like concat(concat('%',#{userName}),'%')
and user_group like concat(concat('%',#{userGroup}),'%')

mapper.update:
update sys_user


        password = #{password}


        , head_img = #{headImg}


where user_id = #{userId}
