mapper.select:
SELECT u.USER_ID,USER_NAME,PHONE,account_level
FROM SYS_USER u
LEFT JOIN (
SELECT user_id,wm_concat(role_name) account_level FROM SYS_USER_ROLE ur LEFT JOIN SYS_ROLE r on r.role_id = ur.role_id GROUP BY user_id
) t on t.USER_ID = u.USER_ID
1=1

        and user_id = #{userId}


        and user_name = #{userName}


        and account_level like concat(concat('%',#{accountLevel}),'%')


        and phone = #{phone}

mapper.update:
update sys_user


        user_name = #{userName}


        , password = #{password}


        , phone = #{phone}


        , account_level = #{accountLevel}


        , remark = #{remark}


where user_id = #{userId}
